$(function(){$.getJSON("https://servicodados.ibge.gov.br/api/v1/localidades/estados/",function(e){for(var t='<option value="" selected disabled>– Selecione seu estado –</option>',o=0;o<e.length;o++)t+='<option data-id="'+e[o].id+'" value="'+e[o].nome+'" >'+e[o].nome+"</option>";$("select[name='uf']").html(t)}),$("select[name='uf']").change(function(){if($(this).val()){const e=$(this).find("option:selected").attr("data-id");$.getJSON("https://servicodados.ibge.gov.br/api/v1/localidades/estados/"+e+"/municipios",{id:$(this).find("option:selected").attr("data-id")},function(e){for(var t='<option value="" disabled selected>– Selecione sua cidade –</option>',o=0;o<e.length;o++)t+='<option value="'+e[o].nome+'" >'+e[o].nome+"</option>";$("select[name='city']").html(t)})}else $("select[name='city']").html('<option value="" disabled selected>– Selecione sua cidade –</option>')})});
